        FusionStorage.consumeCATA(_cata, sender, cataAmount);
        FusionStorage.Layout storage s = FusionStorage.layout();
        s.batchConsumeCars(_cars, sender, ids);
        s.createCar(_cars, sender, carOutputBaseId);
    }
}

